import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createPost } from '../../actions/postActions';
import {Grid, Form, Segment, Button, Header, Message, Icon} from 'semantic-ui-react';

import 'semantic-ui-css/semantic.min.css'


class PostForm extends Component {
  componentDidMount() {
    this.setState({loading:false});

  }
  

  state = {
    name:'',
    description:'',
    reciever:'',
    price:'',
    loading:false,
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit = (e) =>{
    e.preventDefault();

    const post = {
      name:this.state.name,
      description:this.state.description,
      reciever:this.state.reciever,
      price:this.state.price,
     
    };
    this.setState({loading:true});
    this.props.createPost(post);

  }

  render() {

    const {name,description,reciever,price, loading} = this.state; 


    return (
      <div>
  
        <h2>Input Form</h2>
       
          <div>
                  <Form onSubmit = {this.onSubmit}>
                                <Segment stacked>
                                    <Form.Input fluid name='name' icon='gift' iconPosition='left' 
                                    placeholder ='Item Name' onChange = {this.onChange} type = 'text'
                                    value = {name} />

                                    <Form.Input fluid name='description' icon='sticky note' iconPosition='left' 
                                    placeholder ='Note' onChange = {this.onChange} type = 'text'
                                    value = {description}/>

                                    <Form.Input fluid name='reciever' icon='user' iconPosition='left' 
                                    placeholder ='Reciever Name' onChange = {this.onChange} type = 'text'
                                    value = {reciever}/>
                                    
                                    <Form.Input fluid name='price' icon='dollar sign' iconPosition='left' 
                                    placeholder ='Item Price' onChange = {this.onChange} type = 'text'
                                    value = {price}/>
                                    <Button disabled={loading}
                className={loading ? "loading" : ""} color = 'blue' fluid size = 'large' type="submit" >Submit</Button>
                                </Segment>

                            </Form>
            
          </div>
     
    
      <div>
    {/*
        <h1>Add Post</h1>
        <form onSubmit={this.onSubmit}>
          <div>
            <label>Title: </label>
            <br />
            <input
              type="text"
              name="title"
              onChange={this.onChange}
              value={this.state.title}
            />
          </div>
          <br />
          <div>
            <label>Body: </label>
            <br />
            <textarea
              name="body"
              onChange={this.onChange}
              value={this.state.body}
            />
          </div>
          <br />
          <button type="submit">Submit</button>
        </form>*/}
      </div>
      
      </div>
    );
  }
}

PostForm.propTypes = {
  createPost: PropTypes.func.isRequired
};

export default connect(null, { createPost })(PostForm);
