import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchPosts } from '../../actions/postActions';
import {Grid, Form, Segment, Button, Header, Message, Icon} from 'semantic-ui-react';

class Posts extends Component {
  componentWillMount() {
    this.props.fetchPosts();
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.newPost) {
      this.props.posts.unshift(nextProps.newPost);
    }
  }

  render() {
    
    const postItems = this.props.posts.map(post => (
      <div key={post.id}>
    <Segment.Group raised>
      
        <Segment inverted color='blue'> <b>{post.name}</b> </Segment>
        <Segment > Note: {post.description}</Segment>
        <Segment > To: {post.reciever}</Segment>
        <Segment > Price: {post.price}</Segment>
      
        <Button icon = 'edit outline' color = 'yellow' size = 'small' type="submit" ></Button>
        <Button icon = 'window close' color = 'red' size = 'small' type="submit" ></Button>
     
    </Segment.Group>
    <br></br>

    </div>
    ));
    return (
      <div>
        <h2>Shopping List</h2>
        {
          postItems.length === 0 &&
        <h2>
          <i>Your Shopping List Is Empty ... </i>
        </h2>
      }
              
                <i>You Have {postItems.length} Itmes In Your Shopping Cart.</i>

       {postItems}
       
      </div>
    );
  }
}

Posts.propTypes = {
  fetchPosts: PropTypes.func.isRequired,
  posts: PropTypes.array.isRequired,
  newPost: PropTypes.object
};

const mapStateToProps = state => ({
  posts: state.posts.items,
  newPost: state.posts.item
});

export default connect(mapStateToProps, { fetchPosts })(Posts);
