import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createPost } from '../../actions/postActions';
import {Grid, Form, Segment, Button, Header, Message, Icon} from 'semantic-ui-react';

import 'semantic-ui-css/semantic.min.css'


class PostForm extends Component {
  state = {
    name:'',
    description:'',
    reciever:'',
    price:'',
  }




  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit = (e) =>{
    e.preventDefault();

    const post = {
      title: this.state.title,
      body: this.state.body
    };

    this.props.createPost(post);
  }

  render() {

    const {name,description,reciever,price} = this.state; 


    return (
      <div>
      <div className="App">
        <header className="App-header">
        <h1>Input Form</h1>
          <div>
              <Grid textAlign = 'center' verticalAlign ='middle' className='app'>
                <Grid.Column style = {{maxWidth:550}}>
              
                  <Form onSubmit = {this.handleSubmit}>
                                <Segment stacked>
                                    <Form.Input fluid name='name' icon='gift' iconPosition='left' 
                                    placeholder ='Item Name' onChange = {this.onChange} type = 'text'
                                    value = {name} />

                                    <Form.Input fluid name='description' icon='note' iconPosition='left' 
                                    placeholder ='Description' onChange = {this.onChange} type = 'email'
                                    value = {description}/>

                                    <Form.Input fluid name='reciever' icon='user' iconPosition='left' 
                                    placeholder ='Reciever Name' onChange = {this.onChange} type = 'password'
                                    value = {reciever}/>
                                    
                                    <Form.Input fluid name='price' icon='dollar sign' iconPosition='left' 
                                    placeholder ='Item Price' onChange = {this.onChange} type = 'password'
                                    value = {price}/>
                                    <Button color = 'orange' fluid size = 'large'>Submit</Button>
                                </Segment>

                            </Form>
                </Grid.Column>
              </Grid>
          </div>
        </header>
      </div>
      <div>

        <h1>Add Post</h1>
        <form onSubmit={this.onSubmit}>
          <div>
            <label>Title: </label>
            <br />
            <input
              type="text"
              name="title"
              onChange={this.onChange}
              value={this.state.title}
            />
          </div>
          <br />
          <div>
            <label>Body: </label>
            <br />
            <textarea
              name="body"
              onChange={this.onChange}
              value={this.state.body}
            />
          </div>
          <br />
          <button type="submit">Submit</button>
        </form>
      </div>
      </div>
    );
  }
}

PostForm.propTypes = {
  createPost: PropTypes.func.isRequired
};

export default connect(null, { createPost })(PostForm);
